import React, { Component } from 'react';
import Header from '../components/Header'

export default class Home extends Component {
  constructor() {
    super();
    this.state = {
      message: 'Loading...'
    }
  }

  componentDidMount() {
    fetch('/api/home')
      .then(res => res.text())
      .then(res => this.setState({ message: res }));
  }

  render() {
    return (
      <div>
        <Header />
        <h1>Home</h1>
        <p>{this.state.message}</p>
      </div>
    );
  }
}
